if (!defined('_PS_VERSION_')) {
    exit;
}

class Boncompare extends Module
{
    public function __construct()
    {
        $this->name = 'boncompare';
        $this->tab = 'front_office_features';
        $this->version = '1.0.0';
        $this->bootstrap = true;
        $this->author = 'Bonpresta';
        parent::__construct();
        $this->default_language = Language::getLanguage(Configuration::get('PS_LANG_DEFAULT'));
        $this->id_shop = Context::getContext()->shop->id;
        $this->displayName = $this->l('Product Compare');
        $this->description = $this->l('Add compare function for products.');
        $this->ps_versions_compliancy = array('min' => '1.6', 'max' => _PS_VERSION_);
    }

    protected function getConfigurations()
    {
        $configurations = array(
            'BONCOMPARE_ENABLE' => true,
        );

        return $configurations;
    }

    public function install()
    {
        $configurations = $this->getConfigurations();

        foreach ($configurations as $name => $config) {
            Configuration::updateValue($name, $config);
        }

        return parent::install() &&
        $this->registerHook('displaybonCompare') &&
        $this->registerHook('displayTop') &&
        $this->registerHook('displayBeforeBodyClosingTag') &&
        $this->registerHook('displayHeader');
    }

    public function uninstall()
    {
        $configurations = $this->getConfigurations();

        foreach (array_keys($configurations) as $config) {
            Configuration::deleteByName($config);
        }

        return parent::uninstall();
    }

    public function getContent()
    {
        $output = '';
        $result = '';

        if ((bool)Tools::isSubmit('submitSettings')) {
            if (!$result = $this->preValidateForm()) {
                $output .= $this->postProcess();
                $output .= $this->displayConfirmation($this->l('Save all settings.'));
            } else {
                $output = $result;
                $output .= $this->renderTabForm();
            }
        }

        if (!$result) {
            $output .= $this->renderTabForm();
        }
        return $output;
    }

    protected function renderTabForm()
    {
        $fields_form = array(
            'form' => array(
                'legend' => array(
                    'title' => $this->l('Settings'),
                    'icon' => 'icon-cogs',
                ),
                'input' => array(
                    array(
                        'type' => 'switch',
                        'label' => $this->l('Enable:'),
                        'name' => 'BONCOMPARE_ENABLE',
                        'is_bool' => true,
                        'values' => array(
                            array(
                                'id' => 'enable',
                                'value' => 1,
                                'label' => $this->l('Yes')),
                            array(
                                'id' => 'disable',
                                'value' => 0,
                                'label' => $this->l('No')),
                        ),
                    ),
                ),
                'submit' => array(
                    'title' => $this->l('Save'),
                )
            ),
        );
        $helper = new HelperForm();
        $helper->show_toolbar = false;
        $helper->table = $this->table;
        $helper->module = $this;
        $helper->default_form_language = $this->context->language->id;
        $helper->allow_employee_form_lang = Configuration::get('PS_BO_ALLOW_EMPLOYEE_FORM_LANG', 0);
        $helper->identifier = $this->identifier;
        $helper->submit_action = 'submitSettings';
        $helper->currentIndex = AdminController::$currentIndex.'&configure='.$this->name;
        $helper->token = Tools::getAdminTokenLite('AdminModules');
        $helper->tpl_vars = array(
            'fields_value' => $this->getConfigFieldsValues(),
            'languages' => $this->context->controller->getLanguages(),
            'id_language' => $this->context->language->id,
        );
        return $helper->generateForm(array($fields_form));
    }

    public function getConfigFieldsValues()
    {
        $fields = array();
        $configurations = $this->getConfigurations();
        foreach (array_keys($configurations) as $config) {
            $fields[$config] = Configuration::get($config);
        }
        return $fields;
    }

    protected function postProcess()
    {
        $form_values = $this->getConfigFieldsValues();
        foreach (array_keys($form_values) as $key) {
            Configuration::updateValue($key, Tools::getValue($key));
        }
    }

    protected function preValidateForm()
    {
        $errors = array();
        if (count($errors)) {
            return $this->displayError(implode('<br />', $errors));
        }
        return false;
    }

    public function hookDisplayHeader()
    {
        $this->context->controller->addJS($this->_path . '/views/js/boncompare.js');
        $this->context->controller->addCSS($this->_path . '/views/css/boncompare.css');
        Media::addJsDefL('bon_compare_url', $this->_path.'controllers/front/ajax.php');
        Media::addJsDefL('static_token_bon_compare', Tools::getToken(false));
    }

    public function hookDisplayBonCompare()
    {
        if (Configuration::get('BONCOMPARE_ENABLE')) {
            return $this->display($this->_path, '/views/templates/hook/boncompare-button.tpl');
        }
    }

    public function hookDisplayTop()
    {
        if (Configuration::get('BONCOMPARE_ENABLE')) {
            return $this->display($this->_path, '/views/templates/hook/boncompare-top.tpl');
        }
    }

    public function hookDisplayBeforeBodyClosingTag()
    {
        if (Configuration::get('BONCOMPARE_ENABLE')) {
            return $this->display($this->_path, '/views/templates/hook/boncompare-modal.tpl');
        }
    }
}
